function initMap() {
    var location = { lat: 45.0703, lng: 7.6869 }; // Torino coordinates
    var map = new google.maps.Map(document.getElementById("map-canvas"), {
      zoom: 15,
      center: location,
      scrollwheel: false,
    });
    new google.maps.Marker({ position: location, map: map });
  }
  
  function alert_markup(type, message) {
    return (
      '<div class="alert alert-' +
      type +
      '" role="alert">' +
      message +
      '<button type="button" class="close" data-dismiss="alert" aria-label="Close"><span>&times;</span></button></div>'
    );
  }
  
  $(document).ready(function () {
    // Animation waypoints
    ["wp1", "wp2", "wp3", "wp4", "wp5", "wp6", "wp7", "wp8", "wp9"].forEach(function (cls) {
      var anim =
        cls === "wp1" || cls === "wp3" || cls === "wp5" || cls === "wp8"
          ? "fadeInLeft"
          : cls === "wp2" || cls === "wp4" || cls === "wp6" || cls === "wp9"
          ? "fadeInRight"
          : "fadeInUp";
      $("." + cls).waypoint(
        function () {
          $("." + cls).addClass("animated " + anim);
        },
        { offset: "75%" }
      );
    });
  
    // Sliders and tooltips
    $(".flexslider").flexslider({ animation: "slide" });
    $(".single_image").fancybox({ padding: 4 });
    $(".fancybox").fancybox({ padding: 4, width: 1000, height: 800 });
    $('[data-toggle="tooltip"]').tooltip();
  
    // Mobile nav
    $(".nav-toggle").click(function (e) {
      e.preventDefault();
      $(this).toggleClass("active");
      $(".header-nav").toggleClass("open");
    });
  
    $(".header-nav li a").click(function () {
      $(".nav-toggle").removeClass("active");
      $(".header-nav").removeClass("open");
    });
  
    // Sticky nav
    $(window).scroll(function () {
      if ($(window).scrollTop() >= 20) {
        $("section.navigation").addClass("fixed");
        $("header").css({ "border-bottom": "none", padding: "35px 0" });
        $("header .member-actions").css({ top: "26px" });
        $("header .navicon").css({ top: "34px" });
      } else {
        $("section.navigation").removeClass("fixed");
        $("header").css({
          "border-bottom": "solid 1px rgba(255, 255, 255, 0.2)",
          padding: "50px 0",
        });
        $("header .member-actions").css({ top: "41px" });
        $("header .navicon").css({ top: "48px" });
      }
    });
  
    // Smooth scroll
    $('a[href*="#"]:not([href="#"])').click(function () {
      if (
        location.pathname.replace(/^\//, "") ===
          this.pathname.replace(/^\//, "") &&
        location.hostname === this.hostname
      ) {
        var target = $(this.hash);
        target = target.length ? target : $("[name=" + this.hash.slice(1) + "]");
        if (target.length) {
          $("html,body").animate(
            {
              scrollTop: target.offset().top - 90,
            },
            2000
          );
          return false;
        }
      }
    });
  
    // Map toggle
    $("#btn-show-map, #btn-show-content").click(function () {
      $("#map-content").toggleClass("toggle-map-content");
      $("#btn-show-content").toggleClass("toggle-map-content");
    });
  
    // YouTube background
    $(".player").YTPlayer();
  });
  